public class adddigits {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num =5;
		int addall=0;
		while(num>=1){
			addall=addall+num;
			num=num-1;
		}
		System.out.println(addall);
	}

}
