public class evenoddloop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even no list ");
		for(int i=0;i<20;i++)
			
			if (i%2==0){
				
				System.out.println(i);
			}
		System.out.println("\n");
		System.out.println("Odd no list");
		for(int i=0;i<20;i++)
			
			if (i%2!=0){
				System.out.println(i);
			}
			
	}

}

