public class factorial {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int product=1;
		int i=4;
		while (i>=1){
			product=product*i;
			i = i-1;
		}
		System.out.print(product);
		

	}

}
